select a.people_id,a.first_name,a.last_name,a.msisdn,a.created_by,a.mode,a.created_ts,a.gender,a.birthyear,(select group_concat(location_id separator ',') from user_locations where user_id=a.people_id and network_id=?) as locations,(select group_concat(commodity_id separator ',') from user_commodities where user_id=a.people_id and network_id=?) as commodities,(select group_concat(occupation_id separator ',') from user_occupations where user_id=a.people_id and network_id=?) as occupations from people a where default_network_id=? and record_status='U' order by a.first_name;